select count(*) from qna

	where content like #{keyword} or subject like #{keyword}


	where content like #{keyword}


	where subject like #{keyword}

select * from qna 

	where content like #{keyword} or subject like #{keyword}


	where content like #{keyword}


	where subject like #{keyword}

